import { useState } from "react";

function CommentForm({ token, postId, onCommentAdded }) {
    const [content, setContent] = useState("");
    const [error, setError] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!content) return;

        try {
            const res = await fetch("http://localhost:5000/api/comments", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ post_id: postId, content }),
            });

            const data = await res.json();

            if (!res.ok) {
                setError(data.message || "Помилка при додаванні коментаря");
            } else {
                setContent(""); // очистити поле
                setError("");
                onCommentAdded(); // сигналізує App про оновлення
            }
        } catch (err) {
            console.error(err);
            setError("Помилка при запиті до сервера");
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <textarea
                value={content}
                onChange={(e) => setContent(e.target.value)}
                placeholder="Ваш коментар"
                rows={3}
                cols={50}
            />
            <br />
            <button type="submit">Додати коментар</button>
            {error && <p style={{ color: "red" }}>{error}</p>}
        </form>
    );
}

export default CommentForm;
