import { useState } from "react";
import Register from "./components/Register";
import Login from "./components/Login";
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import CommentList from "./components/CommentList";
import CommentForm from "./components/CommentForm";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [selectedPost, setSelectedPost] = useState(1); // тепер можемо змінювати

  return (
    <div>
      <h1>Forum PetProject</h1>

      {!token ? (
        <>
          <Register />
          <Login setToken={setToken} />
        </>
      ) : (
        <>
          <p>Авторизований користувач</p>
          <PostForm token={token} />

          {/* Передаємо setSelectedPost, щоб можна було вибрати пост */}
          <PostList onSelectPost={setSelectedPost} />

          <h3>Коментарі до посту #{selectedPost}</h3>
          <CommentList postId={selectedPost} />

          <CommentForm
            token={token}
            postId={selectedPost}
            onCommentAdded={() => {
              // можна додати логіку перезавантаження коментарів після створення
            }}
          />
        </>
      )}
    </div>
  );
}

export default App;
