import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PostList({ onSelectPost, selectedPostId, refresh }) {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchPosts();
    }, [refresh]);

    const fetchPosts = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/posts');
            setPosts(response.data);
        } catch (error) {
            console.error('Помилка завантаження постів:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="card-gradient rounded-2xl p-8">
                <div className="animate-pulse space-y-4">
                    {[1, 2, 3].map(i => (
                        <div key={i} className="h-24 bg-gradient-to-r from-blue-100 to-purple-100 rounded-xl"></div>
                    ))}
                </div>
            </div>
        );
    }

    return (
        <div className="space-y-4">
            <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold gradient-text">Пости форуму</h2>
                <span className="bg-gradient-to-r from-primary-500 to-accent-500 text-white px-4 py-2 rounded-full text-sm font-semibold shadow-lg">
                    {posts.length} {posts.length === 1 ? 'пост' : 'постів'}
                </span>
            </div>

            {posts.length === 0 ? (
                <div className="card-gradient rounded-2xl p-12 text-center">
                    <div className="w-20 h-20 bg-gradient-to-br from-primary-100 to-accent-100 rounded-full flex items-center justify-center mx-auto mb-4">
                        <svg className="w-10 h-10 text-primary-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                        </svg>
                    </div>
                    <p className="text-gray-500 font-medium">Поки що немає постів. Створіть перший!</p>
                </div>
            ) : (
                <div className="space-y-3">
                    {posts.map(post => (
                        <div
                            key={post.id}
                            onClick={() => onSelectPost(post)}
                            className={`card-gradient rounded-xl p-6 cursor-pointer transition-all duration-300 transform hover:scale-[1.02] ${selectedPostId === post.id
                                ? 'ring-4 ring-primary-400 shadow-xl shadow-primary-500/30'
                                : 'hover:shadow-xl'
                                }`}
                        >
                            <h3 className="text-xl font-bold text-gray-800 mb-2">{post.title}</h3>
                            <p className="text-gray-600 mb-4 line-clamp-2">{post.content}</p>
                            <div className="flex items-center justify-between text-sm">
                                <div className="flex items-center space-x-2">
                                    <div className="w-8 h-8 bg-gradient-to-br from-primary-400 to-accent-400 rounded-full flex items-center justify-center text-white font-bold text-xs">
                                        {post.username?.charAt(0).toUpperCase()}
                                    </div>
                                    <span className="font-semibold text-gray-700">{post.username}</span>
                                </div>
                                <span className="text-gray-500">{new Date(post.created_at).toLocaleDateString('uk-UA')}</span>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default PostList;
