import React, { useState } from 'react';
import axios from 'axios';

function CommentForm({ postId, onCommentAdded }) {
    const [content, setContent] = useState('');
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:5000/api/comments', { postId, content }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setContent('');
            onCommentAdded();
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="card-gradient rounded-2xl p-6 shadow-xl">
            <h3 className="text-xl font-bold gradient-text mb-4">Додати коментар</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
                <textarea value={content} onChange={(e) => setContent(e.target.value)} className="input-field min-h-[100px]" placeholder="Ваш коментар..." required />
                <button type="submit" disabled={loading} className="btn-primary w-full">{loading ? 'Додаємо...' : 'Додати коментар'}</button>
            </form>
        </div>
    );
}
export default CommentForm;
