import { useState, useEffect } from "react";

function CommentList({ postId, reload }) {
    const [comments, setComments] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchComments = async () => {
            try {
                const res = await fetch(`http://localhost:5000/api/comments/${postId}`);
                const data = await res.json();

                if (!res.ok) {
                    setError(data.message || "Помилка при завантаженні коментарів");
                } else {
                    setComments(data);
                    setError("");
                }
            } catch (err) {
                console.error(err);
                setError("Помилка при запиті до сервера");
            }
        };

        fetchComments();
    }, [postId, reload]); // оновлюємо при зміні посту або reload

    return (
        <div style={{ marginTop: "10px", marginBottom: "20px" }}>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {comments.length === 0 && <p>Коментарів немає</p>}
            <ul>
                {comments.map((c) => (
                    <li key={c.id}>
                        <b>Користувач {c.user_id}:</b> {c.content} <i>({new Date(c.created_at).toLocaleString()})</i>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default CommentList;
