import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CommentList({ postId, refresh }) {
    const [comments, setComments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (postId) {
            axios.get(`http://localhost:5000/api/comments/${postId}`)
                .then(res => setComments(res.data))
                .catch(err => console.error(err))
                .finally(() => setLoading(false));
        }
    }, [postId, refresh]);

    if (loading) return <div className="card-gradient rounded-2xl p-8"><div className="animate-pulse space-y-3">{[1, 2, 3].map(i => <div key={i} className="h-16 bg-blue-100 rounded-xl"></div>)}</div></div>;

    return (
        <div className="card-gradient rounded-2xl p-6 shadow-xl">
            <h3 className="text-xl font-bold gradient-text mb-6">Коментарі ({comments.length})</h3>
            {comments.length === 0 ? (
                <p className="text-gray-500 text-center py-8">Коментарів немає</p>
            ) : (
                <div className="space-y-3">
                    {comments.map(comment => (
                        <div key={comment.id} className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl p-4 border border-blue-100">
                            <div className="flex items-center mb-2">
                                <div className="w-8 h-8 bg-gradient-to-br from-primary-400 to-accent-400 rounded-full flex items-center justify-center text-white font-bold text-xs mr-2">
                                    {comment.username?.charAt(0).toUpperCase()}
                                </div>
                                <span className="font-semibold text-gray-700">{comment.username}</span>
                            </div>
                            <p className="text-gray-700">{comment.content}</p>
                            <p className="text-xs text-gray-500 mt-2">{new Date(comment.created_at).toLocaleString('uk-UA')}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
export default CommentList;
