import { useState } from "react";
import Register from "./components/Register";
import Login from "./components/Login";
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import CommentList from "./components/CommentList";
import CommentForm from "./components/CommentForm";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [selectedPost, setSelectedPost] = useState(1); // перший пост для коментарів
  const [reloadComments, setReloadComments] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken("");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Forum PetProject</h1>

      {!token ? (
        <>
          <Register />
          <Login setToken={setToken} />
        </>
      ) : (
        <>
          <p>Авторизований користувач <button onClick={handleLogout}>Вийти</button></p>

          <PostForm token={token} />
          <PostList selectedPost={selectedPost} onSelectPost={setSelectedPost} />

          <h3>Коментарі до посту #{selectedPost}</h3>
          <CommentList postId={selectedPost} reload={reloadComments} />
          <CommentForm
            token={token}
            postId={selectedPost}
            onCommentAdded={() => setReloadComments(prev => !prev)}
          />
        </>
      )}
    </div>
  );
}

export default App;
