import React, { useState } from 'react';
import axios from 'axios';

function PostForm({ onPostCreated }) {
    const [formData, setFormData] = useState({ title: '', content: '' });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:5000/api/posts', formData, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setSuccess('Пост створено!');
            setFormData({ title: '', content: '' });
            onPostCreated();
            setTimeout(() => setSuccess(''), 3000);
        } catch (err) {
            setError('Помилка');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="card-gradient rounded-2xl p-6 shadow-xl">
            <h2 className="text-xl font-bold gradient-text mb-6">Новий пост</h2>
            {error && <div className="mb-4 p-3 bg-red-50 border-l-4 border-red-500 rounded-lg text-red-700">{error}</div>}
            {success && <div className="mb-4 p-3 bg-green-50 border-l-4 border-green-500 rounded-lg text-green-700">{success}</div>}
            <form onSubmit={handleSubmit} className="space-y-4">
                <input type="text" name="title" value={formData.title} onChange={(e) => setFormData({ ...formData, title: e.target.value })} className="input-field" placeholder="Заголовок..." required />
                <textarea name="content" value={formData.content} onChange={(e) => setFormData({ ...formData, content: e.target.value })} className="input-field min-h-[120px]" placeholder="Текст..." required />
                <button type="submit" disabled={loading} className="btn-primary w-full">{loading ? 'Публікуємо...' : 'Опублікувати'}</button>
            </form>
        </div>
    );
}
export default PostForm;
